package number_program;

public class Factorialofnum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num,factorial=1;
		
		
		num = 23;
		for(int x=2;x<=num;x++)
		{
			factorial=factorial*x;
			
		}
		System.out.println(factorial);

	}

}
